\begin{tikzpicture} 
    \begin{axis}[ymin=0, ymax=25, 
                xmin=2006.5, xmax=2010.5,
                ylabel={Average Number of Presents}, 
                xlabel={Year}, 
                axis x line=center,
                axis y line=left,
                clip=false,
                title={\parbox{130pt}{\bfseries\centering Average Number of\\ Holiday Presents Bought\\ by  U.S.\ Shoppers}},
                x label style={at={(axis description cs:0.5,-0.1)},anchor=north},
                /pgf/number format/.cd,
                1000 sep={}] 
        \addplot[mark=none] coordinates {(2007,23.1)} node[above] {$23\frac{1}{10}$};
        \addplot[mark=none] coordinates {(2008,21.5)} node[above] {$21\frac{1}{2}$};
        \addplot[mark=none] coordinates {(2009,18.2)} node[above] {$18\frac{1}{5}$};
        \addplot[mark=none] coordinates {(2010,16.8)} node[above] {$16\frac{4}{5}$};
        \addplot[ybar, fill=blue] coordinates {(2007,23.1) (2008,21.5) (2009,18.2) (2010,16.8)};
    \end{axis}
\end{tikzpicture}

