p.m:
\displaystyle{\begin{aligned}
         -\frac{4}{7} + \frac{23}{28}
              \amp = -\frac{16}{28} + \frac{23}{28} \\
              \amp = \frac{-16 + 23}{28} \\
              \amp = \frac{7}{28} \amp \hbox{We can reduce this by dividing 7 into both the numerator and the denominator.
      }\\
              \amp = \frac{1}{4}
      \end{aligned}
          }

\displaystyle{\begin{aligned}
         -\frac{1}{6}-\frac{7}{18} \amp = -\frac{3}{18}-\frac{7}{18} \\
         \amp = \frac{-3+(-7)}{18} \\
         \amp = -\frac{10}{18} \amp  \hbox{We can divide both the numerator and denominator by 2 to reduce this.
      }\\
         \amp = -\frac{5}{9}
      \end{aligned}
          }

\displaystyle{\begin{aligned}
         -\frac{1}{9} + \frac{2}{45}
              \amp = -\frac{5}{45} + \frac{2}{45} \\
              \amp = \frac{-5 + 2}{45} \\
              \amp = \frac{-3}{45} \amp \hbox{We can reduce this by dividing 3 into both the numerator and the denominator.
      }\\
              \amp = -\frac{1}{15}
      \end{aligned}
          }

\displaystyle{\begin{aligned}
         -\frac{2}{7}-\frac{4}{35} \amp = -\frac{10}{35}-\frac{4}{35} \\
         \amp = \frac{-10+(-4)}{35} \\
         \amp = -\frac{14}{35} \amp  \hbox{We can divide both the numerator and denominator by 7 to reduce this.
      }\\
         \amp = -\frac{2}{5}
      \end{aligned}
          }

\begin{aligned}
                       x^2 = {{\frac{1}{25}}} \amp  \Rightarrow x=-\sqrt{{{\frac{1}{25}}}}\text{ or } x=\sqrt{{{\frac{1}{25}}}}\\
                                          \amp  \Rightarrow x={-{\frac{1}{5}}}\text{ or } x={{\frac{1}{5}}}\\
             \end{aligned}

\begin{aligned}
                      {x^{2}-9x-10}= 0 \amp \Rightarrow (x+1)(x - 10) = 0 \\
                                          \amp  \Rightarrow x+1=0 \text{ or } x - 10=0\\
                                          \amp  \Rightarrow x=-1\text{ or } x=10\\
             \end{aligned}

setup.pg-code:
$a = 4;
$b = 3;
$c = $a + $b;
$expression = Formula("x^($a)*x^($b)");

Context("LimitedPolynomial-Strict");
# custom error message
Context()->{error}{msg}{"A variable can appear only once in each term of a polynomial"}
    = "Your answer must be fully simplified";

$answer = Formula("x^($c)");
$product = $a*$b;
$evaluator = $answer->cmp()->withPostFilter(AnswerHints(
    [Compute("x^($product)")] =>
    "When multiplying terms with the same base, you do not multiply the exponents."));

